    // Toggle xem th√™m th√¥ng s·ªë
    toggleMore() {
      this.showMore = !this.showMore;
    },

    // Toggle m√¥ t·∫£
    toggleDescription() {
      this.showFullDescription = !this.showFullDescription;
    }
  }
